mapper.insert:
insert into token (token, uid, expire_date
  )
values (#{token,jdbcType=VARCHAR}, #{uid,jdbcType=BIGINT}, #{expireDate,jdbcType=TIMESTAMP}
  )

mapper.update:
update token
set uid = #{uid,jdbcType=BIGINT},
  expire_date = #{expireDate,jdbcType=TIMESTAMP}
where token = #{token,jdbcType=VARCHAR}

mapper.select:
select token, uid, expire_date
from token
where token = #{token,jdbcType=VARCHAR}
